import React from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { toast } from 'react-toastify';

import { authenticationService } from '../_services/authentication.service';

class Forgottenpassword extends React.Component {
    constructor(props) {
        super(props);

        // redirect to home if already logged in
        if (authenticationService.currentTokenValue) { 
            this.props.history.push('/');
        }
    }

    success = () => {
      toast.success('Se ha enviado un correo electrónico.', {
        position: "top-right",
        autoClose: 10000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true
        });
    }

    render() {
        return (
            <div>
                <h2>Contraseña olvidada</h2>
                <Formik
                    initialValues={{
                        email: ''
                    }}
                    validationSchema={Yup.object().shape({
                        email: Yup.string()
                            .email('Correo electrónico no válido')
                            .required('Se requiere un correo electrónico')
                    })}
                    onSubmit={({ email }, { setStatus, setSubmitting }) => {
                        setStatus();
                        authenticationService.forgottenPassword(email)
                            .then(
                                () => {
                                    this.success();
                                    const { from } = this.props.location.state || { from: { pathname: "/" } };
                                    this.props.history.push(from);
                                },
                                error => {
                                    setSubmitting(false);
                                    setStatus(error);
                                }
                            );
                    }}
                    render={({ errors, status, touched, isSubmitting }) => (
                        <Form>
                            <div className="form-group">
                                <label htmlFor="email">Correo electrónico</label>
                                <Field name="email" type="email" className={'form-control' + (errors.email && touched.email ? ' is-invalid' : '')} />
                                <ErrorMessage name="email" component="div" className="invalid-feedback" />
                            </div>
                            <div className="form-group">
                                <button type="submit" className="btn btn-primary" disabled={isSubmitting}>Enviar</button>
                                {isSubmitting &&
                                    <img src="data:image/gif;base64," />
                                }
                            </div>
                            {status &&
                                <div className={'alert alert-danger'}>{status}</div>
                            }
                        </Form>
                    )}
                />
            </div>
        )
    }
}

export default Forgottenpassword;